#include <cassert>
#include <cmath>
#include <map>
#include <string>
using namespace std;

map<string, double> compute_operations(int a, int b);

void full_test() {
    map<string, double> res;

    res = compute_operations(10, 5);
    assert(res["addition"] == 15);
    assert(res["subtraction"] == 5);
    assert(res["multiplication"] == 50);
    assert(res["division"] == 2.0);
    assert(res["modulo"] == 0);

    res = compute_operations(5, 0);
    assert(res["addition"] == 5);
    assert(res["subtraction"] == 5);
    assert(res["multiplication"] == 0);
    assert(isnan(res["division"]));
    assert(isnan(res["modulo"]));

    res = compute_operations(0, 0);
    assert(res["addition"] == 0);
    assert(res["subtraction"] == 0);
    assert(res["multiplication"] == 0);
    assert(isnan(res["division"]));
    assert(isnan(res["modulo"]));

    res = compute_operations(0, 10);
    assert(res["addition"] == 10);
    assert(res["subtraction"] == -10);
    assert(res["multiplication"] == 0);
    assert(res["division"] == 0.0);
    assert(res["modulo"] == 0);

    res = compute_operations(-10, 5);
    assert(res["addition"] == -5);
    assert(res["subtraction"] == -15);
    assert(res["multiplication"] == -50);
    assert(res["division"] == -2.0);
    assert(res["modulo"] == 0);

    res = compute_operations(-10, -5);
    assert(res["addition"] == -15);
    assert(res["subtraction"] == -5);
    assert(res["multiplication"] == 50);
    assert(res["division"] == 2.0);
    assert(res["modulo"] == 0);

    res = compute_operations(1000000, 500000);
    assert(res["addition"] == 1500000.0);
    assert(res["subtraction"] == 500000.0);
    assert(res["multiplication"] == 1783790000.0);
    assert(res["division"] == 2.0);
    assert(res["modulo"] == 0);

    res = compute_operations(5, 2);
    assert(res["addition"] == 7);
    assert(res["subtraction"] == 3);
    assert(res["multiplication"] == 10);
    assert(res["division"] == 2.5);
    assert(res["modulo"] == 1);

    res = compute_operations(1, 2);
    assert(res["addition"] == 3);
    assert(res["subtraction"] == -1);
    assert(res["multiplication"] == 2);
    assert(res["division"] == 0.5);
    assert(res["modulo"] == 1);
}

int main() {
    full_test();
    return 0;
}
