List<int> minDifference(int T, List<List<dynamic>> testCases) {
  List<int> results = [];

  for (var caseData in testCases) {
    int N = caseData[0];
    List<int> cards = List<int>.from(caseData[1]);
    cards.sort();
    int pile1 = 0, pile2 = 0;

    for (int i = N - 1; i >= 0; i--) {
      if (pile1 < pile2) {
        pile1 += cards[i];
      } else {
        pile2 += cards[i];
      }
    }

    results.add((pile1 - pile2).abs());
  }

  return results;
}

assertions:
