  List<List<dynamic>> testCases = [
    [4, [1, 2, 3, 4]],
    [5, [5, 6, 7, 8, 9]],
    [3, [10, 20, 30]],
    [1, [100]],
    [6, [1, 1, 1, 1, 1, 1]],
  ];

  List<int> expectedResults = [0, 5, 0, 100, 0];
  List<int> actualResults = minDifference(testCases.length, testCases);

  for (int i = 0; i < expectedResults.length; i++) {
    assert(actualResults[i] == expectedResults[i], 
           'Test case ${i+1} failed: Expected ${expectedResults[i]}, got ${actualResults[i]}');
  }
  print('All tests passed!');
}

testMinDifference();

