// calculateJaccardSimilarity computes the Jaccard similarity coefficient between two strings.
// The Jaccard similarity is defined as the size of the intersection divided by the size of the union
// of the character sets of the two strings. Returns 1.0 if both strings are empty.
double calculateJaccardSimilarity(String str1, String str2) {
  // Convert strings to sets of characters
  final set1 = str1.split('').toSet();
  final set2 = str2.split('').toSet();

  // Calculate intersection and union
  final intersection = set1.intersection(set2);
  final union = set1.union(set2);

  // Handle empty union case (both strings empty)
  if (union.isEmpty) {
    return 1.0;
  }
  return intersection.length / union.length;
}

assertions:
void testCalculateJaccardSimilarity() {
  assert(calculateJaccardSimilarity('hello', 'hallo').toStringAsFixed(4) == '0.6000');
  assert(calculateJaccardSimilarity('abc', 'def').toStringAsFixed(4) == '0.0000');
  assert(calculateJaccardSimilarity('', '').toStringAsFixed(4) == '1.0000');
  assert(calculateJaccardSimilarity('aabbcc', 'abc').toStringAsFixed(4) == '1.0000');
  assert(calculateJaccardSimilarity('123', '12345').toStringAsFixed(4) == '0.6000');
  assert(calculateJaccardSimilarity('dart', 'flutter').toStringAsFixed(4) == '0.2500');
}

void main() {
  testCalculateJaccardSimilarity();
  print('All tests passed!');
}

