// isPermutationPossible determines if it's possible to achieve the target permutation
// with the given number of commands.
//
// Parameters:
// - N: The number of animals
// - target: The target permutation of the animals' positions
// - M: The number of commands available
//
// Returns:
// - "YES" if the target permutation can be achieved, otherwise "NO"
String isPermutationPossible(int N, List<int> target, int M) {
  // We can always achieve the target permutation if M is greater than or equal to N-1.
  // This is because in the worst case, we'd need N-1 swaps to sort the animals into any permutation.
  if (M >= N - 1) {
    return "YES";
  }

  // Check if the target permutation is already sorted
  List<int> sortedTarget = List.from(target)..sort();
  if (listEquals(target, sortedTarget)) {
    return "YES";
  }

  return "NO";
}

// Helper function to compare two lists for equality
bool listEquals(List<int> a, List<int> b) {
  if (a.length != b.length) return false;
  for (int i = 0; i < a.length; i++) {
    if (a[i] != b[i]) return false;
  }
  return true;
}

assertions:
void testIsPermutationPossible() {
  assert(isPermutationPossible(4, [4, 1, 2, 3], 5) == "YES");
  assert(isPermutationPossible(3, [3, 1, 2], 2) == "YES");
  assert(isPermutationPossible(5, [5, 4, 3, 2, 1], 3) == "NO");
  assert(isPermutationPossible(4, [1, 2, 3, 4], 0) == "YES");
  assert(isPermutationPossible(1, [1], 0) == "YES");
}

void main() {
  testIsPermutationPossible();
}

