List<String> sortMixedCharacters(String s) {
  // Separate characters into three categories
  List<String> letters = [];
  List<String> digits = [];
  List<String> specials = [];

  for (int i = 0; i < s.length; i++) {
    String ch = s[i];
    if (RegExp(r'[a-zA-Z]').hasMatch(ch)) {
      letters.add(ch);
    } else if (RegExp(r'[0-9]').hasMatch(ch)) {
      digits.add(ch);
    } else {
      specials.add(ch);
    }
  }

  // Sort letters and digits
  letters.sort();
  digits.sort();

  // Combine results while maintaining original order of special characters
  return [...letters, ...digits, ...specials];
}

assertions:
