void testSortMixedCharacters() {
  assert(sortMixedCharacters("a1b2c3!@#") == ['a', 'b', 'c', '1', '2', '3', '!', '@', '#']);
  assert(sortMixedCharacters("x3y2z1*&^") == ['x', 'y', 'z', '1', '2', '3', '*', '&', '^']);
  assert(sortMixedCharacters("Hello123World!") == ['H', 'W', 'd', 'e', 'l', 'l', 'l', 'o', 'o', 'r', '1', '2', '3', '!']);
  assert(sortMixedCharacters("987abc654") == ['a', 'b', 'c', '4', '5', '6', '7', '8', '9']);
  assert(sortMixedCharacters("!@#\$%^&*") == ['!', '@', '#', '\$', '%', '^', '&', '*']);
  assert(sortMixedCharacters("mixed123with!@#symbols456") == ['b', 'd', 'e', 'h', 'i', 'i', 'l', 'm', 'm', 'o', 's', 's', 't', 'w', 'x', 'y', '1', '2', '3', '4', '5', '6', '!', '@', '#']);
  assert(sortMixedCharacters("").isEmpty);
}

void main() {
  testSortMixedCharacters();
  print("All tests passed!");
}

