// maxSequenceIncrease calculates the maximum length of a contiguous subsequence
// where each subsequent number is greater than the previous one.
// If the input list is empty, it returns 0.
int maxSequenceIncrease(List<int> arr) {
  if (arr.isEmpty) {
    return 0;
  }

  int maxLength = 1;
  int currentLength = 1;

  for (int i = 1; i < arr.length; i++) {
    if (arr[i] > arr[i - 1]) {
      currentLength++;
    } else {
      currentLength = 1;
    }
    if (currentLength > maxLength) {
      maxLength = currentLength;
    }
  }

  return maxLength;
}

assertions:
void testMaxSequenceIncrease() {
  assert(maxSequenceIncrease([]) == 0);
  assert(maxSequenceIncrease([1]) == 1);
  assert(maxSequenceIncrease([1, 2, 3, 4]) == 4);
  assert(maxSequenceIncrease([5, 4, 3, 2, 1]) == 1);
  assert(maxSequenceIncrease([1, 2, 3, 2, 1, 2, 3, 4]) == 4);
  assert(maxSequenceIncrease([1, 1, 1, 1]) == 1);
  assert(maxSequenceIncrease([1, 2, 3, 2, 3, 4, 5]) == 4);
}

void main() {
  testMaxSequenceIncrease();
  print('All tests passed!');
}

