// filterWordsBySymbols processes a list of words by finding the longest matching symbol
// from a given list of symbols in each word and wrapping that symbol in square brackets.
// If no symbols are found in a word, the word remains unchanged.
List<String> filterWordsBySymbols(List<String> words, List<String> symbols) {
  // Helper function to find the longest symbol present in a word
  String findLongestMatch(String word, List<String> symbols) {
    String longestSymbol = "";
    for (String symbol in symbols) {
      if (word.contains(symbol) && symbol.length > longestSymbol.length) {
        longestSymbol = symbol;
      }
    }
    return longestSymbol;
  }

  List<String> transformedWords = [];
  for (String word in words) {
    String longestSymbol = findLongestMatch(word, symbols);
    if (longestSymbol.isNotEmpty) {
      word = word.replaceAll(longestSymbol, "[$longestSymbol]");
    }
    transformedWords.add(word);
  }

  return transformedWords;
}

assertions:
void testFilterWordsBySymbols() {
  // Test Case 1: Basic case with single matches
  assert(filterWordsBySymbols(["hello", "world", "programming"], ["ell", "orl", "pro"]).toString() == 
         '[h[ell]o, w[orl]d, [pro]gramming]');

  // Test Case 2: Multiple possible matches (should pick longest)
  assert(filterWordsBySymbols(["banana", "apple", "orange"], ["an", "ana", "app"]).toString() == 
         '[b[ana]na, [app]le, or[an]ge]');

  // Test Case 3: No matches
  assert(filterWordsBySymbols(["dart", "flutter", "mobile"], ["xyz", "abc"]).toString() == 
         '[dart, flutter, mobile]');

  // Test Case 4: Empty inputs
  assert(filterWordsBySymbols([], ["test"]).toString() == '[]');

  // Test Case 5: Overlapping symbols
  assert(filterWordsBySymbols(["abcdefg"], ["abc", "bcd", "cde"]).toString() == 
         '[[abc]defg]');
}

void main() {
  testFilterWordsBySymbols();
  print('All tests passed!');
}

