// transpose returns the transpose of a given matrix (rows become columns and vice versa)
List<List<dynamic>> transpose(List<List<dynamic>> matrix) {
  return [
    for (int i = 0; i < matrix[0].length; i++)
      [for (List<dynamic> row in matrix) row[i]]
  ];
}

// rotateClockwise rotates a matrix 90 degrees clockwise
List<List<dynamic>> rotateClockwise(List<List<dynamic>> matrix) {
  return [
    for (int i = 0; i < matrix[0].length; i++)
      [for (List<dynamic> row in matrix.reversed) row[i]]
  ];
}

// rotateCounterclockwise rotates a matrix 90 degrees counter-clockwise
List<List<dynamic>> rotateCounterclockwise(List<List<dynamic>> matrix) {
  return [
    for (int i = matrix[0].length - 1; i >= 0; i--)
      [for (List<dynamic> row in matrix) row[i]]
  ];
}

// flipHorizontal flips a matrix horizontally (left becomes right)
List<List<dynamic>> flipHorizontal(List<List<dynamic>> matrix) {
  return [for (List<dynamic> row in matrix) row.reversed.toList()];
}

// flipVertical flips a matrix vertically (top becomes bottom)
List<List<dynamic>> flipVertical(List<List<dynamic>> matrix) {
  return matrix.reversed.toList();
}

// applyMatrixTransformations applies a sequence of transformations to a matrix
List<List<dynamic>> applyMatrixTransformations(
    List<List<dynamic>> matrix, List<String> sequence) {
  for (String command in sequence) {
    switch (command) {
      case "transpose":
        matrix = transpose(matrix);
        break;
      case "rotate_clockwise":
        matrix = rotateClockwise(matrix);
        break;
      case "rotate_counterclockwise":
        matrix = rotateCounterclockwise(matrix);
        break;
      case "flip_horizontal":
        matrix = flipHorizontal(matrix);
        break;
      case "flip_vertical":
        matrix = flipVertical(matrix);
        break;
    }
  }
  return matrix;
}

