class Spreadsheet {
  late int rows;
  late int cols;
  late List<List<int>> sheet;

  Spreadsheet(int rows, int cols) {
    this.rows = rows;
    this.cols = cols;
    sheet = List.generate(rows, (_) => List.filled(cols, 0));
  }

  void setCell(int row, int col, int value) {
    sheet[row][col] = value;
  }

  int getCell(int row, int col) {
    return sheet[row][col];
  }

  int sumRange(int rowStart, int colStart, int rowEnd, int colEnd) {
    int sum = 0;
    for (int i = rowStart; i <= rowEnd; i++) {
      for (int j = colStart; j <= colEnd; j++) {
        sum += sheet[i][j];
      }
    }
    return sum;
  }

  void setRange(int rowStart, int colStart, int rowEnd, int colEnd, int value) {
    for (int i = rowStart; i <= rowEnd; i++) {
      for (int j = colStart; j <= colEnd; j++) {
        sheet[i][j] = value;
      }
    }
  }
}

assertions:
