  spreadsheet.setCell(0, 0, 5);
  spreadsheet.setCell(1, 1, 10);
  spreadsheet.setCell(2, 2, 15);
  assert(spreadsheet.getCell(0, 0) == 5);
  assert(spreadsheet.getCell(1, 1) == 10);
  assert(spreadsheet.getCell(2, 2) == 15);

