String rearrangeString(String s, Map<String, bool> dict) {
  List<String> words = s.split(' ');
  List<String> validWords = [];
  List<String> invalidWords = [];

  for (String word in words) {
    if (dict[word] ?? false) {
      validWords.add(word);
    } else {
      invalidWords.add(word);
    }
  }

  return '${validWords.join(' ')} ${invalidWords.join(' ')}'.trim();
}

assertions:
void testRearrangeString() {
  // Test case 1: Basic case with some valid and invalid words
  Map<String, bool> dict1 = {
    'apple': true,
    'banana': true,
    'orange': false,
    'pear': true,
  };
  assert(rearrangeString('apple orange banana pear grape', dict1) == 
      'apple banana pear orange grape');

  // Test case 2: All words are valid
  Map<String, bool> dict2 = {
    'hello': true,
    'world': true,
    'dart': true,
  };
  assert(rearrangeString('hello world dart', dict2) == 'hello world dart');

  // Test case 3: All words are invalid
  Map<String, bool> dict3 = {
    'cat': false,
    'dog': false,
    'bird': false,
  };
  assert(rearrangeString('cat dog bird', dict3) == 'cat dog bird');

  // Test case 4: Empty string input
  Map<String, bool> dict4 = {
    'word': true,
  };
  assert(rearrangeString('', dict4) == '');

  // Test case 5: Mixed case with punctuation
  Map<String, bool> dict5 = {
    'Hello': true,
    'world!': false,
    'How': true,
    'are': false,
    'you?': true,
  };
  assert(rearrangeString('Hello world! How are you?', dict5) == 
      'Hello How you? world! are');
}

void main() {
  testRearrangeString();
}

