// minTimeToFinalSpeed calculates the minimum time required for all cars to reach
// the final speed, which is the maximum speed in the given list.
// Each car can increase its speed by 1 unit per time unit.
// The function returns the maximum difference between the final speed and any car's initial speed.
int minTimeToFinalSpeed(int N, List<int> speeds) {
  // Find the maximum speed in the list, which will be the final speed for all cars
  int maxSpeed = speeds.reduce((a, b) => a > b ? a : b);
  int time = 0;

  // Calculate the time needed for each car to reach the final speed
  for (int speed in speeds) {
    time = time > (maxSpeed - speed) ? time : (maxSpeed - speed);
  }

  return time;
}

assertions:
void testMinTimeToFinalSpeed() {
  // Test case 1: Normal case with varying speeds
  assert(minTimeToFinalSpeed(4, [3, 5, 2, 7]) == 5);

  // Test case 2: All cars already at maximum speed
  assert(minTimeToFinalSpeed(3, [5, 5, 5]) == 0);

  // Test case 3: Single car
  assert(minTimeToFinalSpeed(1, [10]) == 0);

  // Test case 4: Large difference in speeds
  assert(minTimeToFinalSpeed(5, [1, 10, 100, 1000, 10000]) == 9999);

  // Test case 5: Empty list (edge case)
  try {
    minTimeToFinalSpeed(0, []);
    assert(false); // This line should not be reached
  } catch (e) {
    assert(e.toString().contains('No element'));
  }
}

void main() {
  testMinTimeToFinalSpeed();
  print('All tests passed!');
}

