// sortEvenIndices sorts the elements at even indices in the input array in ascending order,
// while leaving the elements at odd indices unchanged.
// The sorting is done in-place on a copy of the original array to avoid mutation.
List<int> sortEvenIndices(List<int> arr) {
  // Extract values at even indices (0, 2, 4, ...)
  List<int> evenIndicesVals = [];
  for (int i = 0; i < arr.length; i += 2) {
    evenIndicesVals.add(arr[i]);
  }

  // Sort the extracted even index values
  evenIndicesVals.sort();

  // Create a copy of the original array to modify
  List<int> sortedArr = List.from(arr);
  int evenIndex = 0;

  // Replace the even indices in the copy with the sorted values
  for (int i = 0; i < arr.length; i += 2) {
    sortedArr[i] = evenIndicesVals[evenIndex];
    evenIndex++;
  }

  return sortedArr;
}

