  assert(sortEvenIndices([1, 2, 3, 4, 5, 6]) == [1, 2, 3, 4, 5, 6]);
  assert(sortEvenIndices([9, 7, 5, 3, 1]) == [1, 7, 5, 3, 9]);
  assert(sortEvenIndices([0]) == [0]);
  assert(sortEvenIndices([]) == []);
  assert(sortEvenIndices([4, 2, 8, 6, 0, 10, 12, 14]) == [0, 2, 4, 6, 8, 10, 12, 14]);
}

