int minOperationsToAllOnes(int n, int k, String s) {
  int operations = 0;
  int i = 0;
  while (i < n) {
    if (s[i] == '0') {
      operations += 1;
      i += k; // Skip the next k-1 characters because they are flipped
    } else {
      i += 1;
    }
  }
  return operations;
}

assertions:
void testMinOperationsToAllOnes() {
  assert(minOperationsToAllOnes(5, 2, '10101') == 2);
  assert(minOperationsToAllOnes(4, 3, '1111') == 0);
  assert(minOperationsToAllOnes(6, 2, '000000') == 3);
  assert(minOperationsToAllOnes(8, 3, '01010101') == 2);
  assert(minOperationsToAllOnes(1, 1, '0') == 1);
}

void main() {
  testMinOperationsToAllOnes();
}

