import 'dart:collection';

/// Finds the length of the shortest path between two nodes in an unweighted, undirected graph.
/// 
/// The function uses a breadth-first search (BFS) approach to explore the graph level by level,
/// which guarantees finding the shortest path in an unweighted graph.
/// 
/// Parameters:
///   - graph: A Map representing the adjacency list of the graph (key: node, value: list of connected nodes)
///   - start: The starting node
///   - end: The destination node
/// 
/// Returns:
///   - The length of the shortest path as an integer, or -1 if no path exists
int shortestPath(Map<int, List<int>> graph, int start, int end) {
  if (start == end) {
    return 0;
  }

  Set<int> visited = {};
  Queue<List<int>> queue = Queue<List<int>>();
  queue.add([start, 0]); // Each queue element is a pair: [currentNode, currentDistance]

  while (queue.isNotEmpty) {
    List<int> current = queue.removeFirst();
    int currentNode = current[0];
    int currentDistance = current[1];

    if (currentNode == end) {
      return currentDistance;
    }

    visited.add(currentNode);

    for (int neighbor in graph[currentNode] ?? []) {
      if (!visited.contains(neighbor)) {
        queue.add([neighbor, currentDistance + 1]);
        visited.add(neighbor);
      }
    }
  }

  return -1;
}

assertions:
import 'dart:collection';

void testShortestPath() {
  // Test case 1: Simple connected graph
  assert(shortestPath({
    1: [2, 3],
    2: [1, 4],
    3: [1, 4],
    4: [2, 3, 5],
    5: [4]
  }, 1, 5) == 3);

  // Test case 2: Start equals end
  assert(shortestPath({
    1: [2],
    2: [1, 3],
    3: [2]
  }, 2, 2) == 0);

  // Test case 3: Disconnected graph
  assert(shortestPath({
    1: [2],
    2: [1],
    3: [4],
    4: [3]
  }, 1, 3) == -1);

  // Test case 4: Linear graph
  assert(shortestPath({
    1: [2],
    2: [1, 3],
    3: [2, 4],
    4: [3]
  }, 1, 4) == 3);
}

void main() {
  testShortestPath();
  print('All tests passed!');
}

