/// MovingAverage calculates the moving average, maximum, and minimum values
/// within a sliding window of a specified size.
class MovingAverage {
  final int size;
  final Queue<double> queue = Queue();
  double windowSum = 0.0;

  /// Creates a MovingAverage instance with a specified window size.
  MovingAverage(this.size);

  /// Adds a new value to the window and returns the current moving average.
  /// If the window is full, the oldest value is removed before adding the new one.
  double next(double val) {
    if (queue.length == size) {
      windowSum -= queue.removeFirst();
    }
    queue.add(val);
    windowSum += val;
    return windowSum / queue.length;
  }

  /// Returns the maximum value in the current window.
  /// Returns 0 if the window is empty.
  double max() {
    if (queue.isEmpty) return 0.0;
    return queue.reduce((a, b) => a > b ? a : b);
  }

  /// Returns the minimum value in the current window.
  /// Returns 0 if the window is empty.
  double min() {
    if (queue.isEmpty) return 0.0;
    return queue.reduce((a, b) => a < b ? a : b);
  }
}

