/// A class that calculates the moving average of the last `k` numbers in a stream.
class MovingAverage {
  final int k;
  final Queue<int> _queue = Queue<int>();
  double _sum = 0;

  /// Initializes the MovingAverage with the specified window size [k].
  MovingAverage(this.k);

  /// Adds a new value to the stream and returns the moving average of the last `k` numbers.
  double next(int val) {
    _queue.add(val);
    _sum += val;

    if (_queue.length > k) {
      _sum -= _queue.removeFirst();
    }

    return _sum / (_queue.length < k ? _queue.length : k);
  }

  /// Returns the current moving average of the last `k` numbers.
  double getAverage() {
    return _sum / (_queue.length < k ? _queue.length : k);
  }
}

