// mysticSequence calculates the k-th number in a special sequence where:
// - The first two numbers are 1
// - Each subsequent number is the sum of the previous two, modulo 10^9 + 7
// This is similar to the Fibonacci sequence but with modulo operation at each step.
int mysticSequence(int k) {
  const int MOD = 1000000007; // 10^9 + 7
  if (k == 1 || k == 2) {
    return 1;
  }

  int prev = 1;
  int curr = 1;
  for (int i = 3; i <= k; i++) {
    int next = (prev + curr) % MOD;
    prev = curr;
    curr = next;
  }
  return curr;
}

// processTestCases processes multiple test cases by applying the mysticSequence function to each input.
// It takes the number of test cases (t) and a list of integers (ks) as input.
List<int> processTestCases(int t, List<int> ks) {
  return ks.map((k) => mysticSequence(k)).toList();
}

assertions:
void testMysticSequence() {
  // Test individual values
  assert(mysticSequence(1) == 1);
  assert(mysticSequence(2) == 1);
  assert(mysticSequence(5) == 5);
  assert(mysticSequence(100) == 687995182);

  // Test batch processing
  assert(processTestCases(7, [1, 2, 3, 4, 5, 10, 20]).toString() == [1, 1, 2, 3, 5, 55, 6765].toString());
}

void main() {
  testMysticSequence();
  print('All tests passed!');
}

