import 'dart:collection';

// countWaysToDivideTree calculates the number of ways to divide a tree into two parts
// by removing one edge. It uses Depth-First Search (DFS) to calculate the size of
// each subtree and counts how many subtrees have a size that would result in a valid division.
int countWaysToDivideTree(int n, List<List<int>> edges) {
  // Create a graph from the edges using adjacency list representation
  Map<int, List<int>> graph = HashMap();
  for (var edge in edges) {
    int u = edge[0];
    int v = edge[1];
    graph.putIfAbsent(u, () => []);
    graph.putIfAbsent(v, () => []);
    graph[u]!.add(v);
    graph[v]!.add(u);
  }

  // To hold the size of each subtree
  List<int> subtreeSize = List.filled(n + 1, 0);

  // DFS function to calculate subtree sizes
  int dfs(int node, int parent) {
    // Initial size of current node's subtree is 1 (the node itself)
    int size = 1;
    for (int neighbor in graph[node] ?? []) {
      if (neighbor != parent) { // Avoid going back to the parent node
        size += dfs(neighbor, node);
      }
    }
    subtreeSize[node] = size;
    return size;
  }

  // Run DFS from node 1 (or any arbitrary node, here we choose 1)
  dfs(1, -1);

  int ways = 0;
  for (int i = 1; i <= n; i++) {
    // Check the size of this subtree
    if (subtreeSize[i] != n) { // Ensuring we're not at the root
      ways++;
    }
  }

  return ways;
}

void testCountWaysToDivideTree() {
  // Test Case 1: Simple tree with 3 nodes
  assert(countWaysToDivideTree(3, [
    [1, 2],
    [1, 3]
  ]) == 2);

  // Test Case 2: Binary tree with 7 nodes
  assert(countWaysToDivideTree(7, [
    [1, 2],
    [1, 3],
    [2, 4],
    [2, 5],
    [3, 6],
    [3, 7]
  ]) == 6);

  // Test Case 3: Linear tree (linked list) with 4 nodes
  assert(countWaysToDivideTree(4, [
    [1, 2],
    [2, 3],
    [3, 4]
  ]) == 3);

  // Test Case 4: Star-shaped tree with 5 nodes
  assert(countWaysToDivideTree(5, [
    [1, 2],
    [1, 3],
    [1, 4],
    [1, 5]
  ]) == 4);
}

void main() {
  testCountWaysToDivideTree();
}

