// maxPairs calculates the maximum number of pairs that can be formed
// from a given sequence of 'W' (women) and 'M' (men) characters.
// A pair consists of one 'M' and one 'W'.
// The function counts the occurrences of each and returns the minimum count,
// which represents the maximum possible pairs.
int maxPairs(String sequence) {
  int countW = 'W'.allMatches(sequence).length;
  int countM = 'M'.allMatches(sequence).length;

  // The maximum number of pairs is the minimum of the count of 'W' and 'M'
  return countW < countM ? countW : countM;
}

assertions:
void testMaxPairs() {
  assert(maxPairs("WMWM") == 2);
  assert(maxPairs("WWMMM") == 2);
  assert(maxPairs("WWW") == 0);
  assert(maxPairs("MMM") == 0);
  assert(maxPairs("W") == 0);
  assert(maxPairs("M") == 0);
  assert(maxPairs("") == 0);
  assert(maxPairs("WMWMWMWMWM") == 5);
}

void main() {
  testMaxPairs();
}

