// replaceSubstringCaseInsensitive replaces all occurrences of 'toReplace' in 'originalString'
// with 'replacement' in a case-insensitive manner.
// 
// Parameters:
// originalString - The string in which to perform the replacements.
// toReplace - The substring to look for in a case-insensitive manner.
// replacement - The substring to replace with.
//
// Returns:
// A new string with all case-insensitive occurrences of 'toReplace' replaced by 'replacement'.
String replaceSubstringCaseInsensitive(String originalString, String toReplace, String replacement) {
  // Create a regular expression pattern that matches the target string case-insensitively
  final pattern = RegExp(toReplace, caseSensitive: false);
  // Replace all occurrences of the pattern with the replacement string
  return originalString.replaceAll(pattern, replacement);
}

assertions:
void testReplaceSubstringCaseInsensitive() {
  assert(replaceSubstringCaseInsensitive('Hello World', 'hello', 'Hi') == 'Hi World');
  assert(replaceSubstringCaseInsensitive('Dart is great', 'GREAT', 'awesome') == 'Dart is awesome');
  assert(replaceSubstringCaseInsensitive('Case INSENSITIVE', 'insensitive', 'sensitive') == 'Case sensitive');
  assert(replaceSubstringCaseInsensitive('No match here', 'python', 'dart') == 'No match here');
  assert(replaceSubstringCaseInsensitive('Multiple words words words', 'words', 'replacements') == 'Multiple replacements replacements replacements');
  assert(replaceSubstringCaseInsensitive('Mixed CASE exAmple', 'example', 'test') == 'Mixed CASE test');
}

void main() {
  testReplaceSubstringCaseInsensitive();
}

