// isUniqueAnagramIdentifier checks which words in a list are unique anagram identifiers.
// For each word in the input list, it returns true if no other word is an anagram of it,
// and false if there are other anagrams of the word in the list.
// The function uses a map to group words by their sorted characters to identify anagrams.
List<bool> isUniqueAnagramIdentifier(List<String> wordList) {
  Map<String, List<int>> anagramMap = {};
  List<bool> result = List.filled(wordList.length, true);

  for (int idx = 0; idx < wordList.length; idx++) {
    String word = wordList[idx];
    String sortedWord = String.fromCharCodes(word.runes.toList()..sort());

    if (anagramMap.containsKey(sortedWord)) {
      for (int linkedIdx in anagramMap[sortedWord]!) {
        result[linkedIdx] = false;
      }
      result[idx] = false;
      anagramMap[sortedWord]!.add(idx);
    } else {
      anagramMap[sortedWord] = [idx];
    }
  }

  return result;
}

assertions:
void testIsUniqueAnagramIdentifier() {
  assert(isUniqueAnagramIdentifier(["listen", "silent", "enlist", "hello", "world"]) == 
      [false, false, false, true, true]);
  assert(isUniqueAnagramIdentifier(["debitcard", "badcredit", "elvis", "lives", "levis"]) == 
      [false, false, false, false, false]);
  assert(isUniqueAnagramIdentifier(["dormitory", "dirtyroom", "theeyes", "theysee"]) == 
      [false, false, false, false]);
  assert(isUniqueAnagramIdentifier(["cat", "dog", "bird"]) == 
      [true, true, true]);
  assert(isUniqueAnagramIdentifier(["a", "a", "a"]) == 
      [false, false, false]);
}

void main() {
  testIsUniqueAnagramIdentifier();
  print("All tests passed!");
}

