// roadTripSnacks calculates the maximum number of snacks that can be consumed
// without exceeding the calorie limit, and the total calories consumed.
// It sorts the snacks by calorie count and then selects them in order until
// the calorie limit is reached.
List<dynamic> roadTripSnacks(int N, int C, List<int> snacks) {
  // Sort the snacks by calorie count
  snacks.sort();

  int totalCalories = 0;
  int countSnacks = 0;

  for (int calorie in snacks) {
    if (totalCalories + calorie <= C) {
      totalCalories += calorie;
      countSnacks++;
    } else {
      break;
    }
  }

  return [countSnacks, totalCalories];
}

assertions:
void testRoadTripSnacks() {
  // Test case 1: Basic case with 4 snacks
  assert(roadTripSnacks(4, 10, [5, 3, 2, 4]).toString() == '[3, 9]');

  // Test case 2: All snacks can be consumed
  assert(roadTripSnacks(3, 15, [4, 5, 6]).toString() == '[3, 15]');

  // Test case 3: Only one snack can be consumed
  assert(roadTripSnacks(5, 7, [8, 9, 2, 5, 6]).toString() == '[2, 7]');

  // Test case 4: Edge case with no snacks
  assert(roadTripSnacks(0, 10, []).toString() == '[0, 0]');

  // Test case 5: Edge case with zero calorie limit
  assert(roadTripSnacks(3, 0, [1, 2, 3]).toString() == '[0, 0]');
}

void main() {
  testRoadTripSnacks();
  print('All tests passed!');
}

