// phoneCombinations generates all possible letter combinations that the provided digits string could represent.
// It uses backtracking to explore all possible combinations of letters for each digit.
// The digits should be from '2' to '9' inclusive.
List<String> phoneCombinations(String digits) {
  if (digits.isEmpty) {
    return [];
  }

  // Mapping of digit to corresponding letters
  final phoneMap = {
    '2': 'abc',
    '3': 'def',
    '4': 'ghi',
    '5': 'jkl',
    '6': 'mno',
    '7': 'pqrs',
    '8': 'tuv',
    '9': 'wxyz'
  };

  final combinations = <String>[];

  // Helper function for backtracking
  void backtrack(int index, String path) {
    if (index == digits.length) {
      combinations.add(path);
      return;
    }
    final possibleLetters = phoneMap[digits[index]]!;
    for (final letter in possibleLetters.runes) {
      backtrack(index + 1, path + String.fromCharCode(letter));
    }
  }

  backtrack(0, '');
  return combinations;
}

assertions:
void testPhoneCombinations() {
  assert(phoneCombinations("").isEmpty);
  assert(phoneCombinations("2").toString() == "[a, b, c]");
  assert(phoneCombinations("23").toString() == "[ad, ae, af, bd, be, bf, cd, ce, cf]");
  assert(phoneCombinations("79").toString() == 
         "[pw, px, py, pz, qw, qx, qy, qz, rw, rx, ry, rz, sw, sx, sy, sz]");
  assert(phoneCombinations("234").toString() == 
         "[adg, adh, adi, aeg, aeh, aei, afg, afh, afi, bdg, bdh, bdi, beg, beh, bei, bfg, bfh, bfi, cdg, cdh, cdi, ceg, ceh, cei, cfg, cfh, cfi]");
}

void main() {
  testPhoneCombinations();
  print("All tests passed!");
}

