import 'dart:collection';

// Detects if an undirected graph contains a cycle using Depth-First Search (DFS).
// The graph is represented as an adjacency list (Map<int, List<int>>).
// Returns true if a cycle is found, false otherwise.
bool hasCycle(Map<int, List<int>> graph) {
  Set<int> visited = {};

  bool dfs(int v, int? parent) {
    visited.add(v);
    for (int neighbor in graph[v] ?? []) {
      if (!visited.contains(neighbor)) {
        if (dfs(neighbor, v)) {
          return true;
        }
      } else if (parent != neighbor) {
        return true;
      }
    }
    return false;
  }

  for (int node in graph.keys) {
    if (!visited.contains(node)) {
      if (dfs(node, null)) {
        return true;
      }
    }
  }
  return false;
}

// Finds the shortest path between two nodes in an undirected graph using Breadth-First Search (BFS).
// The graph is represented as an adjacency list (Map<int, List<int>>).
// Returns an empty list if no path exists or if start/end nodes are not in the graph.
List<int> findShortestPath(Map<int, List<int>> graph, int start, int end) {
  if (!graph.containsKey(start) || !graph.containsKey(end)) {
    return [];
  }

  Queue<List<int>> queue = Queue()..add([start]);
  Set<int> visited = {start};

  while (queue.isNotEmpty) {
    List<int> path = queue.removeFirst();
    int current = path.last;

    if (current == end) {
      return path;
    }

    for (int neighbor in graph[current] ?? []) {
      if (!visited.contains(neighbor)) {
        visited.add(neighbor);
        queue.add([...path, neighbor]);
      }
    }
  }

  return [];
}

void testGraphFunctions() {
  // Test cases for cycle detection
  Map<int, List<int>> testGraph1 = {
    0: [1, 2],
    1: [0, 2],
    2: [0, 1, 3],
    3: [2],
  };

  Map<int, List<int>> testGraph2 = {
    0: [1],
    1: [0, 2],
    2: [1, 3],
    3: [2],
  };

  Map<int, List<int>> testGraph3 = {
    0: [1],
    1: [0],
    2: [3],
    3: [2],
  };

  assert(hasCycle(testGraph1) == true);
  assert(hasCycle(testGraph2) == false);
  assert(hasCycle(testGraph3) == false);

  // Test cases for shortest path finding
  Map<int, List<int>> testGraph4 = {
    0: [1, 4],
    1: [0, 2, 3, 4],
    2: [1, 3],
    3: [1, 2, 4],
    4: [0, 1, 3],
  };

  assert(findShortestPath(testGraph4, 0, 3).toString() == [0, 1, 3].toString());
  assert(findShortestPath(testGraph4, 2, 4).toString() == [2, 1, 4].toString());
