// canSeeBuilding calculates how many buildings can see at least one other building to their right.
// A building can see another if there exists a building to its right that is of equal or greater height.
// The function takes a list of building heights and returns the count of such buildings.
int canSeeBuilding(List<int> heights) {
  int n = heights.length;
  int visibleCount = 0;

  for (int i = 0; i < n; i++) {
    bool canSee = false;
    for (int j = i + 1; j < n; j++) {
      if (heights[j] >= heights[i]) {
        canSee = true;
        break;
      }
    }
    if (canSee) {
      visibleCount++;
    }
  }

  return visibleCount;
}

