// mergeItems merges a list of items where each item is a list with an ID as the first element.
// Items with the same ID are merged by concatenating their subsequent elements with commas.
// The first item in the input list is treated as a header and preserved as-is.
List<List<dynamic>> mergeItems(List<List<dynamic>> items) {
  // Return early if the list is empty or has only one item (the header)
  if (items.isEmpty || items.length <= 1) {
    return items;
  }

  // Preserve the header from the original list
  List<dynamic> header = items[0];
  Map<dynamic, List<dynamic>> mergedDict = {};

  // Process each item (skipping the header)
  for (List<dynamic> item in items.sublist(1)) {
    dynamic itemId = item[0];

    // If the item ID hasn't been seen before, add it to the dictionary
    if (!mergedDict.containsKey(itemId)) {
      mergedDict[itemId] = item;
    } else {
      // For existing items, merge the subsequent elements
      for (int i = 1; i < item.length; i++) {
        // Only merge if the current element isn't already in the merged item
        if (!mergedDict[itemId]![i].toString().contains(item[i].toString())) {
          mergedDict[itemId]![i] = '${mergedDict[itemId]![i]}, ${item[i]}';
        }
      }
    }
  }

  // Combine the header with the merged items
  return [header] + mergedDict.values.toList();
}

assertions:
void testMergeItems() {
  // Test case 1: No items to merge
  assert(mergeItems([
    ['ID', 'Name', 'Value'],
    [1, 'Item1', '100']
  ]).toString() == [[ID, Name, Value], [1, Item1, 100]].toString());

  // Test case 2: Items with same ID should be merged
  assert(mergeItems([
    ['ID', 'Name', 'Value'],
    [1, 'Item1', '100'],
    [1, 'Item1', '200'],
    [2, 'Item2', '300']
  ]).toString() == [[ID, Name, Value], [1, Item1, 100, 200], [2, Item2, 300]].toString());

  // Test case 3: Items with different IDs should remain separate
  assert(mergeItems([
    ['ID', 'Name', 'Value'],
    [1, 'Item1', '100'],
    [2, 'Item2', '200'],
    [3, 'Item3', '300']
  ]).toString() == [[ID, Name, Value], [1, Item1, 100], [2, Item2, 200], [3, Item3, 300]].toString());

  // Test case 4: Empty input
  assert(mergeItems([]).toString() == [].toString());

  // Test case 5: Only header
  assert(mergeItems([
    ['ID', 'Name', 'Value']
  ]).toString() == [[ID, Name, Value]].toString());
}

void main() {
  testMergeItems();
  print('All tests passed!');
}

