// findBestHike determines the best trail with the least total elevation change.
// It calculates the total elevation change for each trail by summing the absolute
// differences between consecutive points, then returns the trail with the minimum total.
List<int> findBestHike(List<List<int>> trails) {
  // Helper function to calculate total elevation change for a single trail
  int totalElevationChange(List<int> trail) {
    int total = 0;
    for (int i = 0; i < trail.length - 1; i++) {
      total += (trail[i] - trail[i + 1]).abs();
    }
    return total;
  }

  // Find the trail with minimum elevation change
  List<int> bestTrail = trails[0];
  int minChange = totalElevationChange(bestTrail);

  for (List<int> trail in trails) {
    int currentChange = totalElevationChange(trail);
    if (currentChange < minChange) {
      minChange = currentChange;
      bestTrail = trail;
    }
  }

  return bestTrail;
}

assertions:
void testFindBestHike() {
  // Test case 1: Simple trails with obvious best choice
  assert(
    findBestHike([
      [100, 120, 110, 90],
      [100, 105, 110, 115],
      [100, 90, 80, 70]
    ]).toString() == [100, 105, 110, 115].toString()
  );

  // Test case 2: Trails with equal elevation changes
  assert(
    findBestHike([
      [200, 200, 200],
      [150, 150, 150],
      [300, 300, 300]
    ]).toString() == [200, 200, 200].toString()
  );

  // Test case 3: More complex trails
  assert(
    findBestHike([
      [500, 600, 550, 450],
      [400, 350, 400, 450],
      [300, 400, 300, 200]
    ]).toString() == [400, 350, 400, 450].toString()
  );

  // Test case 4: Single-point trails (edge case)
  assert(
    findBestHike([
      [1000],
      [2000],
      [3000]
    ]).toString() == [1000].toString()
  );
}

void main() {
  testFindBestHike();
  print('All tests passed!');
}

