// minNumberOfRoads calculates the minimum number of roads needed to connect all cities
// in a grid layout with n rows and m columns. The formula used is:
// (n - 1) * m + (m - 1) * n, which accounts for horizontal and vertical connections.
List<int> minNumberOfRoads(int t, List<List<int>> testCases) {
  List<int> result = [];
  for (List<int> testCase in testCases) {
    int n = testCase[0];
    int m = testCase[1];
    // Calculate minimum roads: horizontal connections + vertical connections
    int roads = (n - 1) * m + (m - 1) * n;
    result.add(roads);
  }
  return result;
}

