    for (int i = 0; i < m; i++) {
      result.add(times[i].reduce((a, b) => a + b));
    }
  }
  return result;
}

// parseInput processes a multi-line input string into structured datasets.
// Each dataset consists of dimensions (m, n) and a 2D list of times.
// The input stops processing when encountering "0 0".
List<List<dynamic>> parseInput(String inputStr) {
  List<String> lines = inputStr.trim().split('\n');
  List<List<dynamic>> datasets = [];
  int i = 0;
  while (i < lines.length) {
    List<int> dimensions = lines[i].split(' ').map(int.parse).toList();
    int m = dimensions[0];
    int n = dimensions[1];
    if (m == 0 && n == 0) {
      break;
    }
    i++;
    List<List<int>> times = [];
    for (int _ = 0; _ < m; _++) {
      times.add(lines[i].split(' ').map(int.parse).toList());
      i++;
    }
    datasets.add([dimensions, times]);
  }
  return datasets;
}

void testTimeCalculations() {
  // Test case 1: Simple dataset
  String input1 = '''
2 3
1 2 3
4 5 6
0 0
''';
  List<List<dynamic>> dataset1 = parseInput(input1);
  List<int> result1 = calculateTotalTime(dataset1);
  assert(result1.length == 2);
  assert(result1[0] == 6);
  assert(result1[1] == 15);

  // Test case 2: Multiple datasets
  String input2 = '''
1 2
10 20
2 2
1 1
2 2
0 0
''';
  List<List<dynamic>> dataset2 = parseInput(input2);
  List<int> result2 = calculateTotalTime(dataset2);
  assert(result2.length == 3);
  assert(result2[0] == 30);
  assert(result2[1] == 2);
  assert(result2[2] == 4);

  // Test case 3: Empty dataset (only termination)
  String input3 = '''
0 0
''';
  List<List<dynamic>> dataset3 = parseInput(input3);
  List<int> result3 = calculateTotalTime(dataset3);
  assert(result3.isEmpty);
}

void main() {
  testTimeCalculations();
  print('All tests passed!');
}

