  // Test case 1: Compare list with single number
  assert(compareGreater([1, 2, 3, 4], 2).toString() == [false, false, true, true].toString());

  // Test case 2: Compare two lists of equal length
  assert(compareGreater([5, 3, 8, 2], [4, 3, 7, 2]).toString() == [true, false, true, false].toString());

  // Test case 3: All elements greater than comparison number
  assert(compareGreater([10, 20, 30], 5).toString() == [true, true, true].toString());

  // Test case 4: No elements greater than comparison number
  assert(compareGreater([1, 2, 3], 5).toString() == [false, false, false].toString());

  // Test error case (unequal list lengths)
  try {
    compareGreater([1, 2, 3], [1, 2]);
    assert(false); // Should not reach here
  } catch (e) {
    assert(e is ArgumentError);
    assert(e.toString() == 'Invalid argument(s): Both lists must be of the same length.');
  }

  print('All tests passed!');
}

void main() {
  testCompareGreater();
}

