import 'dart:collection';

// Checks if each province in the input is strongly connected from the given city 'c'
// using BFS in both the original graph and its transpose.
List<String> isStronglyConnected(int n, List<List<dynamic>> provinces) {
  // Performs BFS starting from 'start' node and checks if all nodes are reachable
  bool bfs(int start, Map<int, List<int>> graph) {
    Set<int> visited = {};
    Queue<int> queue = Queue.from([start]);

    while (queue.isNotEmpty) {
      int node = queue.removeFirst();
      if (!visited.contains(node)) {
        visited.add(node);
        if (graph.containsKey(node)) {
          queue.addAll(graph[node]!);
        }
      }
    }
    return visited.length == graph.length;
  }

  // Creates the transpose of the given graph (all edges reversed)
  Map<int, List<int>> transposeGraph(Map<int, List<int>> graph) {
    Map<int, List<int>> transposed = {};

    graph.forEach((u, neighbors) {
      for (int v in neighbors) {
        transposed.putIfAbsent(v, () => []).add(u);
      }
    });

    return transposed;
  }

  List<String> results = [];

  for (var province in provinces) {
    int v = province[0];
    int e = province[1];
    int c = province[2];
    List<List<int>> edges = List.castFrom(province[3]);

    Map<int, List<int>> graph = {};
    for (var edge in edges) {
      int u = edge[0];
      int v = edge[1];
      graph.putIfAbsent(u, () => []).add(v);
    }

    bool forwardCheck = bfs(c, graph);
    if (!forwardCheck) {
      results.add("NO");
      continue;
    }

    Map<int, List<int>> transposedGraph = transposeGraph(graph);
    bool backwardCheck = bfs(c, transposedGraph);

    results.add(forwardCheck && backwardCheck ? "YES" : "NO");
  }

  return results;
}

