// Converts a number to currency format with commas and 2 decimal places
// Example: 1234.5 becomes "1,234.50"
String currencyFormat(double number) {
  // Split into whole and decimal parts
  String str = number.toStringAsFixed(2);
  List<String> parts = str.split('.');
  String wholePart = parts[0];
  String decimalPart = parts[1];

  // Add commas for thousands separators
  String formattedWhole = '';
  int length = wholePart.length;
  for (int i = 0; i < length; i++) {
    int digit = length - i;
    formattedWhole += wholePart[i];
    if (digit > 1 && digit % 3 == 1) {
      formattedWhole += ',';
    }
  }

  return '\$$formattedWhole.$decimalPart';
}

// Evaluates a mathematical expression string and returns the result
double evaluateMathExpression(String expression) {
  // This is a simplified evaluator that handles basic arithmetic
  // Note: A real implementation would need a proper expression parser
  // For demonstration, we'll just handle simple cases
  expression = expression.replaceAll(' ', '');

  if (expression.contains('+')) {
    List<String> parts = expression.split('+');
    return double.parse(parts[0]) + double.parse(parts[1]);
  } else if (expression.contains('-')) {
    List<String> parts = expression.split('-');
    return double.parse(parts[0]) - double.parse(parts[1]);
  } else if (expression.contains('*')) {
    List<String> parts = expression.split('*');
    return double.parse(parts[0]) * double.parse(parts[1]);
  } else if (expression.contains('/')) {
    List<String> parts = expression.split('/');
    return double.parse(parts[0]) / double.parse(parts[1]);
  } else {
    return double.parse(expression);
  }
}

assertions:
void testCurrencyAndMathFunctions() {
  // Test currencyFormat function
  assert(currencyFormat(1234.0) == r'$1,234.00');
  assert(currencyFormat(5678.9) == r'$5,678.90');
  assert(currencyFormat(1000000.5) == r'$1,000,000.50');
  assert(currencyFormat(0.99) == r'$0.99');
  assert(currencyFormat(-2500.75) == r'$-2,500.75');

  // Test evaluateMathExpression function
  assert(evaluateMathExpression('100 + 200') == 300.0);
  assert(evaluateMathExpression('500 - 300') == 200.0);
  assert(evaluateMathExpression('50 * 10') == 500.0);
  assert(evaluateMathExpression('1000 / 5') == 200.0);
  assert(evaluateMathExpression('123.45') == 123.45);
}

void main() {
  testCurrencyAndMathFunctions();
  print('All tests passed!');
}

