// distributeCandies calculates the maximum number of groups of three candies that can be formed
// from a list of candy bags, along with the number of leftover candies.
// 
// Parameters:
// - n: The number of bags (unused in calculation but kept for interface consistency)
// - candies: A list of integers representing the number of candies in each bag
//
// Returns:
// A List<int> where the first element is the total number of groups of three,
// and the second element is the number of leftover candies.
List<int> distributeCandies(int n, List<int> candies) {
  int totalGroups = 0;
  int leftoverCandies = 0;

  for (int candy in candies) {
    totalGroups += candy ~/ 3;  // Integer division for groups of three
    leftoverCandies += candy % 3;  // Remainder is leftover candies
  }

  // Convert leftover candies into additional groups if possible
  int additionalGroups = leftoverCandies ~/ 3;
  leftoverCandies = leftoverCandies % 3;

  totalGroups += additionalGroups;

  return [totalGroups, leftoverCandies];
}

assertions:
void testDistributeCandies() {
  // Test case 1: Basic case with exact groups
  assert(distributeCandies(3, [9, 6, 3])[0] == 6);
  assert(distributeCandies(3, [9, 6, 3])[1] == 0);

  // Test Case 2: Mixed groups with leftovers
  assert(distributeCandies(4, [5, 8, 2, 10])[0] == 8);
  assert(distributeCandies(4, [5, 8, 2, 10])[1] == 1);

  // Test Case 3: All small numbers with potential leftover conversion
  assert(distributeCandies(2, [1, 2])[0] == 1);
  assert(distributeCandies(2, [1, 2])[1] == 0);

  // Test Case 4: Edge case with no candies
  assert(distributeCandies(0, [])[0] == 0);
  assert(distributeCandies(0, [])[1] == 0);
}

void main() {
  testDistributeCandies();
  print('All tests passed!');
}

