// It uses depth-first search (DFS) to explore and mark visited cells.
// The function modifies the original maze by marking visited cells with '#'.
int countRegions(List<List<String>> maze, int n, int m) {
  // Nested DFS function to explore a region
  void dfs(int x, int y) {
    // Check if current cell is out of bounds or not part of a region
    if (x < 0 || y < 0 || x >= n || y >= m || maze[x][y] != '.') {
      return;
    }
    // Mark current cell as visited
    maze[x][y] = '#';
    // Explore all four adjacent cells
    dfs(x + 1, y); // down
    dfs(x - 1, y); // up
    dfs(x, y + 1); // right
    dfs(x, y - 1); // left
  }

  int regionsCount = 0;
  // Iterate through each cell in the maze
  for (int i = 0; i < n; i++) {
    for (int j = 0; j < m; j++) {
      // When an unvisited region is found
      if (maze[i][j] == '.') {
        regionsCount++;
        // Explore and mark all connected cells
        dfs(i, j);
      }
    }
  }
  return regionsCount;
}

