// canMakeEqualByOneSubstitution checks if two strings can be made equal by either:
// 1. Swapping any two characters in one string (when sorted versions are equal), or
// 2. Making exactly one character substitution in one string.
List<String> canMakeEqualByOneSubstitution(int t, List<List<String>> testCases) {
  List<String> results = [];

  for (List<String> testCase in testCases) {
    String s1 = testCase[0];
    String s2 = testCase[1];

    // Convert strings to lists of characters, sort them, and compare
    List<String> s1Sorted = s1.split('')..sort();
    List<String> s2Sorted = s2.split('')..sort();

    if (s1Sorted.join() == s2Sorted.join()) {
      results.add("YES");
    } else if (canBeEqualWithOneSubstitution(s1, s2)) {
      results.add("YES");
    } else {
      results.add("NO");
    }
  }

  return results;
}

// canBeEqualWithOneSubstitution checks if two strings can be made equal by
// changing exactly one character in one string.
bool canBeEqualWithOneSubstitution(String s1, String s2) {
  if (s1.length != s2.length) {
    return false;
  }

  Map<String, int> counter = {};

  // Count characters in s1
  for (int i = 0; i < s1.length; i++) {
    String char = s1[i];
    counter[char] = (counter[char] ?? 0) + 1;
  }

  int mismatchPairs = 0;

  // Check characters in s2 against the counter
  for (int i = 0; i < s2.length; i++) {
    String char = s2[i];
    if (counter.containsKey(char) && counter[char]! > 0) {
      counter[char] = counter[char]! - 1;
    } else {
      mismatchPairs++;
    }
  }

  // If there's exactly one mismatch, one substitution can fix it
  return mismatchPairs == 1;
}

assertions:
