  int t = 4;
  List<List<String>> testCases = [
    ["abc", "bca"],
    ["aabb", "abac"],
    ["abc", "def"],
    ["abcd", "abce"],
  ];

  List<String> expectedResults = ["YES", "YES", "NO", "YES"];
  List<String> actualResults = canMakeEqualByOneSubstitution(t, testCases);

  for (int i = 0; i < expectedResults.length; i++) {
    assert(actualResults[i] == expectedResults[i]);
  }
}

void main() {
  testCanMakeEqualByOneSubstitution();
  print("All tests passed!");
}

