import 'dart:math';

// processText processes the given text string and returns a map with word count,
// sorted unique words, and total number of words.
//
// Parameters:
//   text (String): A string of text to be processed.
//
// Returns:
//   Map<String, dynamic>: A map with keys 'wordCount', 'uniqueWords', and 'totalWords'.
Map<String, dynamic> processText(String text) {
  // Step 1: Normalize the text by converting it to lowercase
  text = text.toLowerCase();

  // Step 2: Remove all punctuation (replace with spaces to separate words instead of joining)
  text = text.replaceAll(RegExp(r'[^\w\s]'), ' ');

  // Step 3: Split the text into individual words
  List<String> words = text.split(RegExp(r'\s+')).where((word) => word.isNotEmpty).toList();

  // Step 4: Count the occurrences of each unique word
  Map<String, int> wordCount = {};
  for (String word in words) {
    wordCount.update(word, (value) => value + 1, ifAbsent: () => 1);
  }

  // Step 5: Create a list of all unique words sorted in alphabetical order
  List<String> uniqueWords = wordCount.keys.toList()..sort();

  // Step 6: Calculate the total number of words
  int totalWords = words.length;

  // Step 7: Return the results as a map
  return {
    'wordCount': wordCount,
    'uniqueWords': uniqueWords,
    'totalWords': totalWords,
  };
}

