// countSubsequenceOccurrences counts how many times a subsequence appears
// within a main sequence as a contiguous segment.
// 
// Parameters:
//   mainSequence: List of integers representing the main sequence.
//   subsequence: List of integers representing the subsequence to find.
// Returns:
//   An integer count of how many times the subsequence appears in the main sequence.
int countSubsequenceOccurrences(List<int> mainSequence, List<int> subsequence) {
  int n = mainSequence.length;
  int m = subsequence.length;

  // If either sequence is empty or subsequence is longer than main sequence,
  // return 0 as no matches are possible
  if (n == 0 || m == 0 || m > n) {
    return 0;
  }

  int count = 0;

  // Check each possible starting position in the main sequence
  for (int i = 0; i <= n - m; i++) {
    bool match = true;

    // Check if the current window matches the subsequence
    for (int j = 0; j < m; j++) {
      if (mainSequence[i + j] != subsequence[j]) {
        match = false;
        break;
      }
    }

    if (match) {
      count++;
    }
  }

  return count;
}

assertions:
void testCountSubsequenceOccurrences() {
  assert(countSubsequenceOccurrences([1, 2, 3, 4, 1, 2, 3], [1, 2, 3]) == 2);
  assert(countSubsequenceOccurrences([1, 1, 1, 1, 1], [1, 1]) == 4);
  assert(countSubsequenceOccurrences([5, 6, 7, 8, 9], [6, 7, 8]) == 1);
  assert(countSubsequenceOccurrences([1, 2, 3], [4, 5, 6]) == 0);
  assert(countSubsequenceOccurrences([], [1, 2]) == 0);
  assert(countSubsequenceOccurrences([1, 2, 3, 4], []) == 0);
  assert(countSubsequenceOccurrences([1, 2, 3, 1, 2, 3, 1, 2, 3], [1, 2, 3]) == 3);
}

void main() {
  testCountSubsequenceOccurrences();
  print('All tests passed!');
}

