// maxNestingDepth calculates the maximum nesting depth of parentheses in a given string.
// It assumes the input string contains only valid parentheses (balanced and properly nested).
// The function iterates through each character, tracking the current depth and updating the maximum depth encountered.
int maxNestingDepth(String s) {
  int maxDepth = 0;
  int currentDepth = 0;

  for (int i = 0; i < s.length; i++) {
    if (s[i] == '(') {
      currentDepth++;
      if (currentDepth > maxDepth) {
        maxDepth = currentDepth;
      }
    } else if (s[i] == ')') {
      currentDepth--;
    }
  }

  return maxDepth;
}

assertions:
void testMaxNestingDepth() {
  assert(maxNestingDepth("()") == 1);
  assert(maxNestingDepth("()()") == 1);
  assert(maxNestingDepth("(())") == 2);
  assert(maxNestingDepth("(()())") == 2);
  assert(maxNestingDepth("((()))") == 3);
  assert(maxNestingDepth("((()()))()(())") == 3);
  assert(maxNestingDepth("") == 0);
}

void main() {
  testMaxNestingDepth();
}

