// gcd calculates the greatest common divisor of two integers using the Euclidean algorithm.
// The function recursively applies the Euclidean algorithm until the remainder is zero.
// If either number is negative, it uses their absolute values for the calculation.
int gcd(int a, int b) {
  // Take absolute values to handle negative numbers
  a = a.abs();
  b = b.abs();

  // Base case: when b is 0, the GCD is a
  if (b == 0) {
    return a;
  } 
  // Recursive case: apply Euclidean algorithm
  else {
    return gcd(b, a % b);
  }
}

assertions:
void testGCD() {
  assert(gcd(48, 18) == 6);
  assert(gcd(0, 5) == 5);
  assert(gcd(5, 0) == 5);
  assert(gcd(17, 23) == 1);
  assert(gcd(60, 48) == 12);
  assert(gcd(-36, 24) == 12);
  assert(gcd(36, -24) == 12);
  assert(gcd(-36, -24) == 12);
  assert(gcd(0, 0) == 0);
}

void main() {
  testGCD();
  print('All tests passed!');
}

