// replaceSubstrings replaces all occurrences of keys in the replacements map
// with their corresponding values in the given text.
// 
// Parameters:
//   text - the input string to process
//   replacements - a map where keys are substrings to be replaced and values are their replacements
// Returns:
//   A new string with all replacements applied
String replaceSubstrings(String text, Map<String, String> replacements) {
  String result = text;
  replacements.forEach((oldStr, newStr) {
    result = result.replaceAll(oldStr, newStr);
  });
  return result;
}

assertions:
void testReplaceSubstrings() {
  // Test case 1: Basic replacements
  assert(replaceSubstrings(
    "Hello world! Welcome to the world of programming.",
    {"world": "Dart", "programming": "coding"}
  ) == "Hello Dart! Welcome to the Dart of coding.");

  // Test case 2: Overlapping replacements
  assert(replaceSubstrings(
    "apple app application",
    {"app": "APP", "apple": "FRUIT", "application": "SOFTWARE"}
  ) == "APPle APP APPlication");

  // Test case 3: No replacements
  assert(replaceSubstrings(
    "This text remains unchanged",
    {"foo": "bar", "baz": "qux"}
  ) == "This text remains unchanged");

  // Test case 4: Empty string
  assert(replaceSubstrings(
    "",
    {"something": "nothing"}
  ) == "");
}

void main() {
  testReplaceSubstrings();
  print("All tests passed!");
}

