// findPeakCells identifies all peak cells in a 2D grid.
// A peak cell is defined as a cell whose value is strictly greater than all its adjacent cells
// (top, bottom, left, right). The function returns the coordinates of these peaks in lexicographical order.
List<List<int>> findPeakCells(int m, int n, List<List<int>> grid) {
  // Helper function to check if a cell is a peak
  bool isPeak(int i, int j) {
    // Check top neighbor
    if (i > 0 && grid[i][j] <= grid[i - 1][j]) {
      return false;
    }
    // Check bottom neighbor
    if (i < m - 1 && grid[i][j] <= grid[i + 1][j]) {
      return false;
    }
    // Check left neighbor
    if (j > 0 && grid[i][j] <= grid[i][j - 1]) {
      return false;
    }
    // Check right neighbor
    if (j < n - 1 && grid[i][j] <= grid[i][j + 1]) {
      return false;
    }
    return true;
  }

  List<List<int>> peakCells = [];

  // Iterate through each cell in the grid
  for (int i = 0; i < m; i++) {
    for (int j = 0; j < n; j++) {
      if (isPeak(i, j)) {
        peakCells.add([i, j]);
      }
    }
  }

  // Sort the peaks in lexicographical order
  peakCells.sort((a, b) {
    if (a[0] != b[0]) {
      return a[0].compareTo(b[0]);
    }
    return a[1].compareTo(b[1]);
  });

  return peakCells;
}

assertions:
void testFindPeakCells() {
  // Test case 1
  assert(
    findPeakCells(3, 3, [
      [1, 4, 3],
      [3, 2, 1],
      [5, 6, 2],
    ]).toString() == '[[0, 1], [2, 1]]',
  );

  // Test case 2
  assert(
    findPeakCells(2, 2, [
      [1, 1],
      [1, 1],
    ]).toString() == '[]',
  );

  // Test case 3
  assert(
    findPeakCells(4, 3, [
      [10, 20, 15],
      [21, 30, 14],
      [7, 16, 32],
      [1, 2, 3],
    ]).toString() == '[[1, 1], [2, 2]]',
  );

  // Test case 4
  assert(
    findPeakCells(1, 1, [
      [5],
    ]).toString() == '[[0, 0]]',
  );
}

void main() {
  testFindPeakCells();
  print('All tests passed!');
}

