import 'dart:collection';

/// updateMatrix calculates the distance of each cell to the nearest 0 in the given matrix.
/// It uses a breadth-first search (BFS) approach starting from all 0 cells simultaneously.
/// The function returns a new matrix with the same dimensions where each cell contains
/// the minimum distance to the nearest 0 from the original matrix.
List<List<int>> updateMatrix(List<List<int>> mat) {
  if (mat.isEmpty || mat[0].isEmpty) {
    return [];
  }

  int m = mat.length;
  int n = mat[0].length;
  List<List<int>> dist = List.generate(m, (_) => List.filled(n, 0x7FFFFFFF));
  Queue<List<int>> queue = Queue();

  // Initialize the queue with all 0 positions and set their distance to 0
  for (int i = 0; i < m; i++) {
    for (int j = 0; j < n; j++) {
      if (mat[i][j] == 0) {
        dist[i][j] = 0;
        queue.add([i, j]);
      }
    }
  }

  // Define the four possible movement directions (up, down, left, right)
  List<List<int>> directions = [
    [-1, 0],
    [1, 0],
    [0, -1],
    [0, 1]
  ];

  // Process the queue using BFS
  while (queue.isNotEmpty) {
    List<int> cell = queue.removeFirst();
    int x = cell[0], y = cell[1];

    for (List<int> dir in directions) {
      int xi = x + dir[0];
      int yi = y + dir[1];

      // Check if the new position is within bounds
      if (xi >= 0 && xi < m && yi >= 0 && yi < n) {
        // Update the distance if a shorter path is found
        if (dist[xi][yi] > dist[x][y] + 1) {
          dist[xi][yi] = dist[x][y] + 1;
          queue.add([xi, yi]);
        }
      }
    }
  }

  return dist;
}

