// minOperationsToTransform calculates the minimum number of operations needed to transform string S into string T.
// The operations allowed are:
// 1. Cyclic shift of the entire string (counts as 1 operation)
// 2. Swapping any two characters (counts as 1 operation per swap)
// Returns -1 if the transformation is not possible due to character mismatch.
int minOperationsToTransform(int N, String T, String S) {
  // First check if both strings have the same characters (same frequency of each character)
  if (!_haveSameCharacters(T, S)) {
    return -1;
  }

  // Check if a single cyclic shift can transform T into S
  for (int i = 0; i < N; i++) {
    String shifted = T.substring(i) + T.substring(0, i);
    if (shifted == S) {
      return 1;
    }
  }

  // If no single cyclic shift works, return 2 as per the problem's examples
  return 2;
}

// Helper function to check if two strings have the same characters with the same frequencies
bool _haveSameCharacters(String a, String b) {
  if (a.length != b.length) return false;

  Map<String, int> charCount = {};

  // Count characters in string a
  for (int i = 0; i < a.length; i++) {
    String char = a[i];
    charCount[char] = (charCount[char] ?? 0) + 1;
  }

  // Subtract counts using string b
  for (int i = 0; i < b.length; i++) {
    String char = b[i];
    if (!charCount.containsKey(char) || charCount[char] == 0) {
      return false;
    }
    charCount[char] = charCount[char]! - 1;
  }

  return true;
}

assertions:
void testMinOperationsToTransform() {
  assert(minOperationsToTransform(3, 'abc', 'cab') == 1);
  assert(minOperationsToTransform(4, 'abcd', 'dabc') == 1);
  assert(minOperationsToTransform(3, 'abc', 'bac') == 2);
  assert(minOperationsToTransform(3, 'abc', 'def') == -1);
  assert(minOperationsToTransform(5, 'aabbb', 'bbbaa') == 1);
  assert(minOperationsToTransform(4, 'abcd', 'badc') == 2);
}

void main() {
  testMinOperationsToTransform();
  print('All tests passed!');
}

