  while (i < n) {
    // If current character is '1' and next character is '1', we have to flip one of them
    if (i + 1 < n && binaryString[i] == '1' && binaryString[i + 1] == '1') {
      operations += 1;
      // Skip the next character to avoid consecutive '1's
      i += 2;
    } else {
      i += 1;
    }
  }

  return operations;
}

void testMaxOperationsToAvoidAdjacentOnes() {
  assert(maxOperationsToAvoidAdjacentOnes('1101') == 1);
  assert(maxOperationsToAvoidAdjacentOnes('1111') == 2);
  assert(maxOperationsToAvoidAdjacentOnes('1010') == 0);
  assert(maxOperationsToAvoidAdjacentOnes('0000') == 0);
  assert(maxOperationsToAvoidAdjacentOnes('1110111') == 2);
  assert(maxOperationsToAvoidAdjacentOnes('1') == 0);
  assert(maxOperationsToAvoidAdjacentOnes('0') == 0);
  assert(maxOperationsToAvoidAdjacentOnes('110011001') == 2);
}

