import 'dart:math';

// Finds the nearest neighbor to the target point using brute force approach.
// Returns null if the points list is empty.
List<double>? bruteForceNearest(List<List<double>> points, List<double> target) {
  if (points.isEmpty) {
    return null;
  }

  double minDistance = double.infinity;
  List<double>? nearestPoint;

  for (var point in points) {
    double distance = _calculateDistance(point, target);
    if (distance < minDistance) {
      minDistance = distance;
      nearestPoint = point;
    }
  }

  return nearestPoint;
}

// KD-Tree Node class to store points and child nodes
class KDTreeNode {
  final List<double> point;
  final KDTreeNode? left;
  final KDTreeNode? right;

  KDTreeNode({required this.point, this.left, this.right});
}

// Finds the nearest neighbor to the target point using KD-Tree approach.
// Returns null if the points list is empty.
List<double>? kdTreeNearest(List<List<double>> points, List<double> target) {
  if (points.isEmpty) {
    return null;
  }

  // Builds a KD-Tree from the given points
  KDTreeNode buildKdTree(List<List<double>> points, [int depth = 0]) {
    if (points.isEmpty) {
      throw StateError("Empty points list in buildKdTree");
    }

    int k = points[0].length; // Assumes all points have the same dimension
    int axis = depth % k;

    // Sort points based on current axis
    points.sort((a, b) => a[axis].compareTo(b[axis]));
    int median = points.length ~/ 2;

    return KDTreeNode(
      point: points[median],
      left: median > 0 ? buildKdTree(points.sublist(0, median), depth + 1) : null,
      right: median + 1 < points.length
          ? buildKdTree(points.sublist(median + 1), depth + 1)
          : null,
    );
  }

  // Recursive helper function to find nearest neighbor in KD-Tree
  (List<double>, double) kdTreeNearestRec(KDTreeNode? node, List<double> target,
      [int depth = 0,
      List<double>? bestNode,
      double bestDist = double.infinity]) {
    if (node == null) {
      return (bestNode ?? [], bestDist);
    }

    int k = target.length;
    int axis = depth % k;
    List<double> point = node.point;

    double d = _calculateDistance(point, target);
    if (d < bestDist) {
      bestDist = d;
      bestNode = point;
    }

    double diff = target[axis] - point[axis];

    if (diff <= 0) {
      var (newBestNode, newBestDist) =
          kdTreeNearestRec(node.left, target, depth + 1, bestNode, bestDist);
      if (newBestDist < bestDist) {
        bestDist = newBestDist;
        bestNode = newBestNode;
      }
      if (diff.abs() < bestDist) {
        var (rightBestNode, rightBestDist) = kdTreeNearestRec(
            node.right, target, depth + 1, bestNode, bestDist);
        if (rightBestDist < bestDist) {
          bestDist = rightBestDist;
          bestNode = rightBestNode;
        }
      }
    } else {
      var (newBestNode, newBestDist) =
          kdTreeNearestRec(node.right, target, depth + 1, bestNode, bestDist);
      if (newBestDist < bestDist) {
        bestDist = newBestDist;
        bestNode = newBestNode;
      }
      if (diff.abs() < bestDist) {
        var (leftBestNode, leftBestDist) = kdTreeNearestRec(
            node.left, target, depth + 1, bestNode, bestDist);
        if (leftBestDist < bestDist) {
          bestDist = leftBestDist;
          bestNode = leftBestNode;
        }
      }
    }

    return (bestNode ?? [], bestDist);
  }

  final kdTreeRoot = buildKdTree(points);
  final (nearestPoint, _) = kdTreeNearestRec(kdTreeRoot, target);
  return nearestPoint;
}

// Helper function to calculate Euclidean distance between two points
double _calculateDistance(List<double> a, List<double> b) {
  if (a.length != b.length) {
    throw ArgumentError("Points must have the same dimension");
  }
  double sum = 0;
  for (int i = 0; i < a.length; i++) {
    sum += pow(a[i] - b[i], 2);
  }
  return sqrt(sum);
}

